mapper.update:
update employee
set name = #{name,jdbcType=VARCHAR},
  sn = #{sn,jdbcType=VARCHAR},
  salary = #{salary,jdbcType=DECIMAL},
  deptId = #{deptId,jdbcType=BIGINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, name, sn, salary, deptId
from employee
where id = #{id,jdbcType=BIGINT}
